import React from 'react';
import useLocalStorage from 'react-use-localStorage';

class LStorage extends React.Component {

  constructor(){
    super();
    this.state = {
        name : ''
    }
  }

  handleChange = (e) => {
    e.preventDefault();
    this.setState({[e.target.name] : e.target.value });
  }

  handleSubmit = (e) => {
    e.preventDefault();
    this.state.name = useLocalStorage('name' , 'Initial Value');
  }

  render() {
    return (
     <div>
     <h1>Set Name to store in Local Storage</h1>
     <form onSubmit={this.handleSubmit}>
        <input onChange = {this.handleChange} type='text' name='name' value={this.state.name} placeholder='Enter your name' />
        <input type='submit' value='Save' />
     </form>
     </div>
    );
  }
};

export default LStorage;
